import React, { useState } from 'react';
import { Form, Formik, Field } from 'formik';
import 'bootstrap/dist/css/bootstrap.min.css';


import * as Bs from 'react-bootstrap';

import { BasicInput, SideButton, RegularButton, ReactSwitch, MultipleInput} from './FormComponents.js';

const ReactCard = () => {

    const [cluster, setCluster] = useState(false);

    return(
        <Formik
            initialValues={{ 
                reqst_cd: '123',
                workflows: [],
                cluster_switch: cluster
            }}
            onSubmit={values =>
                setTimeout(() => {
                alert(JSON.stringify(values, null, 2));
                }, 500)
            }
            render={({ values }) => (
                <Bs.Container className="mt-5 mb-5">
                    <Bs.Card>
                        <Bs.Card.Body>
                            <Bs.Card.Title>Extract Script for Transport</Bs.Card.Title>
                                <Form>
                                        <Field col="6" name="reqst_cd" type />
                                        <Bs.Row>
                                            <Bs.Col md="6">
                                                <Bs.Form.Group>
                                                    <Field 
                                                        as={Bs.Form.Control} 
                                                        type="text" 
                                                        id="reqst_cd" 
                                                        name="reqst_cd" 
                                                        placeholder="First Name"
                                                        col="6"
                                                    />
                                                </Bs.Form.Group>
                                            </Bs.Col>
                                        </Bs.Row>
                                        {/* Insert Workflow Array Input here */}
                                        <MultipleInput value={values} name="workflows" item="workflow" placeholder="Workflow Code" />
                                        <RegularButton col="5" type="submit" size="lg" text="Extract" />

                                </Form>
                        </Bs.Card.Body>
                    </Bs.Card>
                </Bs.Container>
            )}
        />
    );
};

export default ReactCard;
