import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import {BasicInput, SideButton, RegularButton} from './FormComponents.js';
import Row from 'react-bootstrap/Row';

class ReactCard extends React.Component{

    constructor(props){
        super(props);

        this.state = {
            reqst_cd: "",
            wflow_cd: ""
        }
        
    }

    render(){
        return(
            <Container className="mt-5 mb-5">
                <Card>
                    <Card.Body>
                        <Card.Title>Extract Script for Transport</Card.Title>
                        <Row>
                            <BasicInput value={this.state.reqst_cd} col="6" id="reqst_cd" name="reqst_cd" placeholder="Request Code" type="email" />
                        </Row>
                        <Row>
                            <BasicInput value={this.state.wflow_cd} col="6" id="wflow_cd" name="wflow_cd" placeholder="Workflow Code" type="text" />
                            <SideButton col="1" id="add_wflow" name="add_wflow"/>
                            <RegularButton col="5" type="submit" />
                        </Row>
                    </Card.Body>
                </Card>
            </Container>
        );
    }
}

export default ReactCard;
