import React, { useState } from 'react';
import { Form, Formik, Field } from 'formik';
import 'bootstrap/dist/css/bootstrap.min.css';


import * as Bs from 'react-bootstrap';

import { BasicInput, SideButton, RegularButton, ReactSwitch, MultipleInput} from './FormComponents.js';

const ReactCard = () => {


    return(
        <Formik
            initialValues={{ 
                reqst_cd: '',
                workflows: [],
                cluster_switch: false,
                clusters: []
            }}
            onSubmit={values =>
                setTimeout(() => {
                alert(JSON.stringify(values, null, 2));
                }, 500)
            }
        >
                <Bs.Container className="mt-5 mb-5">
                    <Bs.Card>
                        <Bs.Card.Body>
                            <Bs.Card.Title>Extract Script for Transport</Bs.Card.Title>
                            {formik => (
                                <Form>
                                    <BasicInput
                                        as={Bs.Form.Control}
                                        type="text"
                                        id="reqst_cd"
                                        name="reqst_cd"
                                        placeholder="Request Code"
                                        col="6"
                                    />
                                    <MultipleInput value={values} name="workflows" item="workflow" placeholder="Workflow Code" />
                                    
                                    <ReactSwitch label="With Cluster?" name="cluster_switch" id="cluster_switch" col="5" />
                                    <RegularButton col="5" type="submit" size="lg" text="Extract" />
                                </Form>
                                )}
                        </Bs.Card.Body>
                    </Bs.Card>
                </Bs.Container>
        </Formik>
    );
};

export default ReactCard;
