import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import * as Bs from 'react-bootstrap';
import {FaPlus, FaMinus} from 'react-icons/fa';
import { useField, Field, FieldArray, ErrorMessage  } from 'formik';
import '../App.css';


function InputWithLabel(props){
    return(
        <>
            <Bs.Col md="6">
                <Bs.Form.Group>
                    <FormLabel label={props.label} />
                    <Field 
                        as={props.comp} 
                        type={props.type}
                        id={props.id}
                        name={props.name}
                        placeholder={props.placeholder}
                    />
                    <div className="text-danger">
                        <ErrorMessage name={props.name} />
                    </div>
                </Bs.Form.Group>
            </Bs.Col>
        </>
    );
}

function BasicInput(props){
    return(
        <>
            <Bs.Col md="6">
                <Bs.Form.Group>
                    <Field 
                        as={props.comp} 
                        type={props.type}
                        id={props.id}
                        name={props.name}
                        placeholder={props.placeholder}
                    />
                    {
                        typeof props.errors === 'string' ?
                            null
                        :
                            <Bs.Row>
                                <Bs.Col md="12" className="text-danger">
                                    <Bs.Form.Group>
                                        <ErrorMessage name={props.name} />
                                    </Bs.Form.Group>
                                </Bs.Col>
                            </Bs.Row>
                    }
                    
                </Bs.Form.Group>
            </Bs.Col>
        </>
    );
}

function SideButton(props){
    var icon = "";
    var variant = "";
    if(props.use === "add"){
        icon = <FaPlus />;
        variant = "outline-primary";
    }
    else{
        icon = <FaMinus />;
        variant = "outline-danger";
    }
    return(
        <>
            <Bs.Col md={props.col}>
                <Bs.Form.Group>
                    <Bs.Button 
                        variant={variant} 
                        className="btn-circle" 
                        onClick={props.onClick}
                    >
                        {icon}
                    </Bs.Button>
                </Bs.Form.Group>
            </Bs.Col>
        </>
    );
}

function FormButton(props){
    return(
        <>
            <Bs.Col md={props.col}>
                <Bs.Form.Group>
                    <Bs.Button 
                        variant="outline-primary" 
                        size={props.size} 
                        type={props.type} 
                        onClick={props.onClick}
                    >
                        {props.text}
                    </Bs.Button>
                </Bs.Form.Group>
            </Bs.Col>
        </>
    );
}

function RegularButton(props){
    var className = "mx-auto text-center";
    if(props.type !== "submit"){
        className = "";
    }

    return(
        <>
            <Bs.Col md={props.col} className={className}>
                <Bs.Form.Group>
                    <Bs.Button 
                        variant="outline-primary" 
                        size={props.size} 
                        type={props.type} 
                        onClick={props.onClick}
                    >
                        {props.text}
                    </Bs.Button>
                </Bs.Form.Group>
            </Bs.Col>
        </>
    );
}

function ReactSwitch(props){
    const [ field, meta, helpers] = useField(props.name);
    const { value } = meta;
    const { setValue } = helpers;
    const { fields } = field;
    return(
        <>
            <Bs.Row>
                <Bs.Col md={props.col}>
                    <Bs.Form.Group>
                        <Bs.Form.Check 
                            type="switch"
                            id={props.id}
                            name={props.name}
                            label={props.label}
                            checked={value}
                            onChange={() => setValue(!value)}
                        />
                    </Bs.Form.Group>
                </Bs.Col>
            </Bs.Row>
        </>
    );
}

function ConditionInput(props){

    if(props.show){
        return(
            <> 
                <Bs.Row>
                    <InputWithLabel
                        comp={Bs.Form.Control}
                        type={props.type}
                        id={props.id}
                        name={props.name}
                        placeholder={props.placeholder}
                        label={props.label}
                    />
                </Bs.Row>
            </>
        );
    }
        return null;
}

function FormLabel(props){
    return(
        <Bs.Form.Label>{props.label}</Bs.Form.Label>
    );

}

function MultipleInput(props){
    
    return(
        <>
            <FieldArray
                name={props.name}
                render={
                    arrayHelpers => (
                        <div>
                            <FormLabel label={props.label} />
                            {
                                
                                props.value && props.value.length > 0 ? (
                                    props.value.map((value, index) => (
                                        <Bs.Row key={index}>
                                                <Field 
                                                    as={BasicInput} 
                                                    comp={Bs.Form.Control} 
                                                    name={`${props.name}.${index}`} 
                                                    placeholder={props.placeholder}
                                                    parent={props.name}
                                                    errors={props.errors}
                                                />
                                                <SideButton
                                                    col="1"
                                                    use="subtract"
                                                    onClick={() => arrayHelpers.remove(index)} 
                                                />
                                                <SideButton
                                                    col="1"
                                                    use="add"
                                                    onClick={() => arrayHelpers.push('')}
                                                />
                                        </Bs.Row>
                                    ))
                                ) : 
                                (
                                    <Bs.Row>
                                        <FormButton 
                                            type="button" 
                                            size="small" 
                                            onClick={() => arrayHelpers.push('')} 
                                            text={props.button}
                                            name={props.name}
                                            errors={props.errors}
                                        /> 
                                    </Bs.Row>
                                )
                            }
                            {
                                typeof props.errors === 'string' ?
                                    <Bs.Row>
                                        <Bs.Col md="6" className="text-danger">
                                            <Bs.Form.Group>
                                                <ErrorMessage name={props.name} />
                                            </Bs.Form.Group>
                                        </Bs.Col>
                                    </Bs.Row>
                                :
                                    null
                            }
                        </div>
                    )
                }
            />
        </>
    );
}

export  {
    BasicInput,
    SideButton,
    RegularButton,
    ReactSwitch,
    MultipleInput,
    FormLabel,
    InputWithLabel,
    ConditionInput
};
