import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import {FaPlus} from 'react-icons/fa';
import { Field } from 'formik';
import '../App.css';

function BasicInput(props){
    return(
        <>
            <Col md={props.col}>
                <Form.Group>
                    <Form.Control name={props.name} id={props.id} type={props.type} value={props.value} onChange={props.onChange} placeholder={props.placeholder} />
                </Form.Group>
            </Col>
        </>
    );
}

function SideButton(props){
    return(
        <>
            <Col md={props.col}>
                <Form.Group>
                    <Button variant="outline-primary" className="btn-circle"><FaPlus /></Button>
                </Form.Group>
            </Col>
        </>
    );
}

function RegularButton(props){
    return(
        <>
            <Col md={props.col} className="mx-auto text-center">
                <Form.Group>
                    <Button variant="outline-primary" size="lg" type={props.type}>Extract</Button>
                </Form.Group>
            </Col>
        </>
    );
}

function ReactSwitch(props){
    return(
        <>
            <Col md={props.col}>
                <Form.Group>
                    <Form.Check 
                        type="switch"
                        label={props.label}
                        checked={props.checked}
                        onChange={props.onChange}
                    />
                </Form.Group>
            </Col>
        </>
    );
}

const MySwitch = ({ props, handleClick }) => (
    <>
        <Col md={props.col}>
            <Form.Group>
                <Form.Check 
                    type="switch"
                    label={props.label}
                    checked={props.checked}
                    onClick={() => handleClick()}
                />
            </Form.Group>
        </Col>
    </>
);

const fieldName = "cluster_switch";

const CustomSwitch = () => (
  <Field name={fieldName} id={fieldName} type="switch">
    {({ field: { value }, form: { setFieldValue } }) => (
        <MySwitch
        label="With cluster?"
        checked={false}
        handleClick={number => setFieldValue(fieldName, number)}
        />
    )}
  </Field>
);


export  {
    BasicInput,
    SideButton,
    RegularButton,
    CustomSwitch
};
