import React, { useState } from 'react';
import { useFormik, Form, Formik, Field, FieldArray } from 'formik';
import 'bootstrap/dist/css/bootstrap.min.css';
// import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import {BasicInput, SideButton, RegularButton, ReactSwitch} from './FormComponents.js';
import Row from 'react-bootstrap/Row';

const ReactCard = () => {

    // const [cluster, setCluster] = useState(false);

    // const formik = useFormik({
    //     initialValues: {
    //       reqst_cd: '',
    //       wflow_cd: '',
    //       workflows: ['1'],
    //       cluster_switch: cluster
    //     },
    //     enableReinitialize: true,
    //     onSubmit: values => {
    //       alert(JSON.stringify(values, null, 2));
    //     },
    // });

    
    // return(
    //     <Container className="mt-5 mb-5">
    //         <Card>
    //             <Card.Body>
    //                 <Form onSubmit={formik.handleSubmit}>
    //                     <Card.Title>Extract Script for Transport</Card.Title>
    //                     <Row>
    //                         <BasicInput id="reqst_cd" name="reqst_cd" value={formik.values.reqst_cd} onChange={formik.handleChange} col="6" placeholder="Request Code" type="text" />
    //                     </Row>
    //                     <Row>
    //                         <BasicInput id="wflow_cd" name="wflow_cd" value={formik.values.wflow_cd} onChange={formik.handleChange} col="6" placeholder="Workflow Code" type="text" />
    //                     </Row>
    //                     <FieldArray
    //                         name="workflows"
    //                         render={arrayHelpers => (
    //                             formik.values.workflows.map((cd, index) => (
    //                                 <Row key={`workflows_${index}`}>
    //                                     <BasicInput
    //                                         name={`workflows_${index}`}
    //                                         id={`workflows_${index}`}
    //                                         type="text"
    //                                         value={cd}
    //                                         placeholder="Workflow Code"
    //                                     />
    //                                 </Row>
    //                             ))
    //                         )}
    //                     />
                        
                            
                        
    //                     <Row>
    //                         <ReactSwitch id="cluster_switch" name="cluster_switch" label="With Cluster?" checked={cluster} onChange={() => setCluster(!cluster)} />
    //                     </Row>
    //                     <Row>
    //                         <RegularButton col="10" type="submit" />
    //                     </Row>
    //                 </Form>
    //             </Card.Body>
    //         </Card>
    //     </Container>
    // );

    const [cluster, setCluster] = useState(false);

    return(

        

        <Formik
        initialValues={{
            reqst_cd: '',
            wflow_cd: '',
            workflows: ['1'],
            cluster_switch: cluster
        }}
        onSubmit={values =>
            setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            }, 500)
        }
        render={({ values }) => (
            <Container className="mt-5 mb-5">
                <Card>
                <Card.Body>
                    <Card.Title>Extract Script for Transport</Card.Title>
                    <Form>
                        <Row>
                            <Field as={BasicInput} type="text" id="reqst_cd" name="reqst_cd" placeholder="Request Code" col="6" />
                            {/* <BasicInput id="reqst_cd" name="reqst_cd" value={values.reqst_cd} onChange={handleChange} col="6" placeholder="Request Code" type="text" /> */}
                        </Row>
                        <Row>
                        <Field as={BasicInput} type="text" id="reqst_cd" name="reqst_cd" placeholder="Request Code" col="6" />  
                            <BasicInput id="wflow_cd" name="wflow_cd" value={values.wflow_cd} onChange={handleChange} col="6" placeholder="Workflow Code" type="text" />
                        </Row>
                        <FieldArray
                            name="friends"
                            render={arrayHelpers => (
                            <div>
                                {values.friends && values.friends.length > 0 ? (
                                values.friends.map((friend, index) => (
                                    <div key={index}>
                                    <Field name={`friends.${index}`} />
                                    <button
                                        type="button"
                                        onClick={() => arrayHelpers.remove(index)} // remove a friend from the list
                                    >
                                        -
                                    </button>
                                    <button
                                        type="button"
                                        onClick={() => arrayHelpers.insert(index, '')} // insert an empty string at a position
                                    >
                                        +
                                    </button>
                                    </div>
                                ))
                                ) : (
                                <button type="button" onClick={() => arrayHelpers.push('')}>
                                    {/* show this when user has removed all friends from the list */}
                                    Add a friend
                                </button>
                                )}
                                <div>
                                <button type="submit">Submit</button>
                                </div>
                            </div>
                            )}
                        />
                    </Form>
                </Card.Body>
                </Card>
            </Container>
        )}
        />
    );
};

export default ReactCard;
