import React, { useState } from 'react';
import { useFormik, Form, Formik, Field, FieldArray } from 'formik';
import 'bootstrap/dist/css/bootstrap.min.css';
// import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import {BasicInput, SideButton, RegularButton, ReactSwitch} from './FormComponents.js';
import Row from 'react-bootstrap/Row';

const ReactCard = () => {

    // const [cluster, setCluster] = useState(false);

    const [cluster, setCluster] = useState(false);
    return(
        <Formik
            initialValues={{ 
                reqst_cd: '',
                wflow_cd: '',
                workflows: [],
                cluster_switch: cluster
            }}
            onSubmit={values =>
                setTimeout(() => {
                alert(JSON.stringify(values, null, 2));
                }, 500)
            }
            render={({ values }) => (
                <Container className="mt-5 mb-5">
                    <Card>
                        <Card.Body>
                            <Card.Title>Extract Script for Transport</Card.Title>
                            <Form>
                                <Row>
                                    <Field 
                                        as={BasicInput} 
                                        type="text" 
                                        id="reqst_cd" 
                                        name="reqst_cd" 
                                        placeholder="Request Code" 
                                        col="6" 
                                    />
                                </Row>
                                <FieldArray
                                    name="workflows"
                                    render={arrayHelpers => (
                                    <div>
                                        {values.workflows && values.workflows.length > 0 ? (
                                        values.workflows.map((workflow, index) => (
                                            <div key={index}>
                                            <Field name={`workflows.${index}`} />
                                            <button
                                                type="button"
                                                onClick={() => arrayHelpers.remove(index)} // remove a friend from the list
                                            >
                                                -
                                            </button>
                                            <button
                                                type="button"
                                                onClick={() => arrayHelpers.push('')} // insert an empty string at a position
                                            >
                                                +
                                            </button>
                                            </div>
                                        ))
                                        ) : (
                                        <button type="button" onClick={() => arrayHelpers.push('')}>
                                            {/* show this when user has removed all friends from the list */}
                                            Add a friend
                                        </button>
                                        )}
                                        <div>
                                        <button type="submit">Submit</button>
                                        </div>
                                    </div>
                                    )}
                                />
                            </Form>
                        </Card.Body>
                    </Card>
                </Container>
            )}
        />
    );
};

export default ReactCard;
