import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import {FaPlus} from 'react-icons/fa';
import '../App.css';

function BasicInput(props){
    return(
        <>
            <Col md={props.col}>
                <Form.Group>
                    <Form.Control name={props.name} id={props.id} type={props.type} value={props.value} onChange={props.onChange} placeholder={props.placeholder} />
                </Form.Group>
            </Col>
        </>
    );
}

function SideButton(props){
    return(
        <>
            <Col md={props.col}>
                <Form.Group>
                    <Button variant="outline-primary" className="btn-circle"><FaPlus /></Button>
                </Form.Group>
            </Col>
        </>
    );
}

function RegularButton(props){
    return(
        <>
            <Col md={props.col} className="mx-auto text-center">
                <Form.Group>
                    <Button variant="outline-primary" size="lg" type={props.type}>Extract</Button>
                </Form.Group>
            </Col>
        </>
    );
}

function ReactSwitch(props){
    return(
        <>
            <Col md={props.col}>
                <Form.Group>
                    <Form.Check 
                        type="switch"
                        id={props.id}
                        name={props.name}
                        label={props.label}
                        checked={props.checked}
                        Change={props.onClick}
                    />
                </Form.Group>
            </Col>
        </>
    );
}

export  {
    BasicInput,
    SideButton,
    RegularButton,
    ReactSwitch
};
