import React from 'react';
import {Container, Row, Table, Card, ButtonToolbar} from 'react-bootstrap';
import OptionBtn from './TableComponents';

class Files extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      files: [],
      alert_class: '',
      alert_heading: '',
      alert_body: '',
      alert_show: false
    };
  }

  componentDidMount() {
    fetch("http://10.15.10.250:8080/extract_dev2/files")
    .then(res => res.json())
    .then(
        (result) => {
            this.setState({
                files: result
            });
        },
        (error) => {
            this.setState({
                error
            });
            console.log(this.state.error)
        }
    )
  }

  filesChangeHandler(){
    fetch("http://10.15.10.250:8080/extract_dev2/files")
    .then(res => res.json())
    .then(
        (result) => {
            if(result['error']){
              this.setState({
                alert_show: true,
                alert_class: 'alert-danger',
                alert_heading: 'Error',
                alert_body: result['data']
              });
            }
            else{
              this.setState({
                alert_show: false,
                alert_class: 'alert-success',
                alert_heading: 'Success',
                alert_body: result['data']
              });
            }
        },
        (error) => {
            this.setState({
                error
            });
            // console.log(this.state.error)
        }
    )
  }

  downloadBtnClickHandler(file){
    setTimeout(() => {
      const response = {
        file: "http://10.15.10.250:8080/extract_dev2/download/"+ file,
      };
      window.location.href = response.file;
    }, 100);
  }

  deleteBtnClickHandler(file){
    fetch("http://10.15.10.250:8080/extract_dev2/delete/"+file)
    .then(res => res.json())
    .then(
        (result) => {
            if(result['error']){
              this.setState({
                alert_show: true,
                alert_class: 'alert-danger',
                alert_heading: 'Error',
                alert_body: result['data']
              });
            }
            else{
              this.setState({
                alert_show: false,
                alert_class: 'alert-success',
                alert_heading: 'Success',
                alert_body: result['data']
              });
              this.filesChangeHandler();
            }
        },
        (error) => {
            this.setState({
                error
            });
            // console.log(this.state.error)
        }
    )
  }

  render() {
    return(
      <>
        <Container className="mt-5 mb-5">
            <Card className="p-2">
              <Card.Body>
                <Row className="justify-content-md-center">
                  <Card.Title>Extracted Workflow Scripts</Card.Title>
                </Row>
                <Row className="justify-content-md-center">
                  <Table striped bordered hover variant="dark">
                    <thead>
                      <tr>
                        <th>Filename</th>
                        <th className="text-center">Options</th>
                      </tr>
                    </thead>
                    <tbody>
                      {
                        this.state.files.map((value, index) => (
                          <tr key={index}>
                            <td>{value}</td>
                            <td className="text-center">
                              <ButtonToolbar>
                                <OptionBtn use={1} onClick={() => this.downloadBtnClickHandler(value)} />
                                <OptionBtn use={2} onClick={() => this.deleteBtnClickHandler(value)} />
                              </ButtonToolbar>
                            </td>
                          </tr>
                        ))
                      }
                    </tbody>
                  </Table>
                </Row>
              </Card.Body>
            </Card>
        </Container>
      </>
    );
  }
}
  
  export default Files;
