import React from 'react';
import {Container, Row, Table, Card} from 'react-bootstrap';
import TableRow from './TableRow';

class Files extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      files: []
    };
  }

  componentDidMount() {
    fetch("http://10.15.10.250:8080/extract_dev2/files")
    .then(res => res.json())
    .then(
        (result) => {
            // console.log(result)
            this.setState({
                files: result
            });
        },
        (error) => {
            this.setState({
                error
            });
            console.log(this.state.error)
        }
    )
  }

  filesChangeHandler(){
    fetch("http://10.15.10.250:8080/extract_dev2/files")
    .then(res => res.json())
    .then(
        (result) => {
            
            this.setState({
                files: result['files']
            });
            console.log(this.state.files)
        },
        (error) => {
            this.setState({
                error
            });
            // console.log(this.state.error)
        }
    )
  }

  render() {
    const arr = this.state.files;
    // console.log(this.state.files)
    return(
      <>
        <Container className="mt-5 mb-5">
            <Card className="p-2">
              <Card.Body>
                <Row className="justify-content-md-center">
                  <Card.Title>Extracted Workflow Scripts</Card.Title>
                </Row>
                <Row className="justify-content-md-center">
                  <Table striped bordered hover variant="dark">
                    <thead>
                      <tr>
                        <th>Filename</th>
                        <th>Options</th>
                      </tr>
                    </thead>
                    <tbody>
                      {
                        arr.map((value, index) => (
                          <tr key={index}>
                            <td>{value['name']}</td>
                            <td>^ -</td>
                          </tr>
                        ))
                      }
                      {/* <TableRow files={this.state.files} /> */}
                    </tbody>
                  </Table>
                </Row>
              </Card.Body>
            </Card>
        </Container>
      </>
    );
  }
}
  
  export default Files;
