import React from 'react';
import { useFormik } from 'formik';
import 'bootstrap/dist/css/bootstrap.min.css';
import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import {BasicInput, SideButton, RegularButton, ReactSwitch} from './FormComponents.js';
import Row from 'react-bootstrap/Row';

const ReactCard = () => {

    const formik = useFormik({
        initialValues: {
          reqst_cd: '',
          wflow_cd: '',
          wflow_switch: false,
          workflows: [],
        },
        onSubmit: values => {
          alert(JSON.stringify(values, null, 2));
        },
    });

    
    return(
        <Container className="mt-5 mb-5">
            <Card>
                <Card.Body>
                    <Form onSubmit={formik.handleSubmit}>
                        <Card.Title>Extract Script for Transport</Card.Title>
                        <Row>
                            <BasicInput id="reqst_cd" name="reqst_cd" value={formik.values.reqst_cd} onChange={formik.handleChange} col="6" placeholder="Request Code" type="text" />
                        </Row>
                        <Row>
                            <BasicInput id="wflow_cd" name="wflow_cd" value={formik.values.wflow_cd} onChange={formik.handleChange} col="6" placeholder="Workflow Code" type="text" />
                            <SideButton col="1" id="add_wflow" name="add_wflow"/>
                        </Row>
                        <Row>
                            <ReactSwitch id="wflow_switch" name="wflow_switch" labe="With cluster?" col="6" />
                        </Row>
                        <Row>
                            <RegularButton col="10" type="submit" />
                        </Row>
                    </Form>
                </Card.Body>
            </Card>
        </Container>
    );
};

export default ReactCard;
