import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import * as Bs from 'react-bootstrap';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import {FaPlus, FaMinus} from 'react-icons/fa';
import { useFormik, Form, Formik, Field, FieldArray } from 'formik';
import '../App.css';

function MyForm(){
    return(
        <>
            <Form>

            </Form>
        </>
    );
}


function BasicInput(props){
    return(
        <>
            <Row>
                <Col md={props.col}>
                    <Form.Group>
                        <Form.Control 
                            name={props.name} 
                            id={props.id} 
                            type={props.type}
                            placehold={props.placeholder}
                        />
                    </Form.Group>
                </Col>
            </Row>
        </>
    );
}

function SideButton(props){
    
    return(
        <>
            <Col md={props.col}>
                <Form.Group>
                    <Button variant="outline-primary" className="btn-circle" onClick={props.onClick}><FaPlus /></Button>
                </Form.Group>
            </Col>
        </>
    );
}

function RegularButton(props){
    return(
        <>
            <Col md={props.col} className="mx-auto text-center">
                <Form.Group>
                    <Button variant="outline-primary" size="lg" type={props.type}>Extract</Button>
                </Form.Group>
            </Col>
        </>
    );
}

function ReactSwitch(props){
    return(
        <>
            <Col md={props.col}>
                <Form.Group>
                    <Form.Check 
                        type="switch"
                        id={props.id}
                        name={props.name}
                        label={props.label}
                        checked={props.checked}
                        onChange={props.onChange}
                    />
                </Form.Group>
            </Col>
        </>
    );
}

function MultipleInput(props){
    return(
        <>
            <FieldArray
                name={props.name}
                render={arrayHelpers => (
                <div>
                    {props.value.workflows && props.value.workflows.length > 0 ? (
                    props.value.workflows.map((value, index) => (
                        <Row key={index}>
                            <Col md="5">
                                <Field name={`${props.name}.${index}`} />
                            </Col>
                            <SideButton
                                col="1"
                                use="subtract"
                                onClick={() => arrayHelpers.remove(index)} // remove a friend from the list
                            />
                            <SideButton
                                col="1"
                                use="add"
                                onClick={() => arrayHelpers.push('')} // remove a friend from the list
                            />
                        </Row>
                    ))
                    ) : 
                    (
                        <Row>
                            <button type="button" onClick={() => arrayHelpers.push('')}>
                                {/* show this when user has removed all friends from the list */}
                                Add a friend
                            </button>
                        </Row>
                    )}
                    </div>
                )}
            />
        </>
    );
}

export  {
    MyForm,
    BasicInput,
    SideButton,
    RegularButton,
    ReactSwitch,
    MultipleInput
};
