import React from 'react';
import { Form, Formik } from 'formik';
import 'bootstrap/dist/css/bootstrap.min.css';
import * as Bs from 'react-bootstrap';
import * as Yup from 'yup';

import { RegularButton, MultipleInput, InputWithLabel} from './FormComponents.js';

const SignupSchema = Yup.object().shape({
    reqst_cd: Yup.string()
        .required('Required'),
    workflows: Yup.string()
        .required('Required'),
    clusters: Yup.string()
        .required('Required'),
});

const ReactCard = () => {


    return(
        <Formik
            initialValues={{ 
                reqst_cd: '',
                workflows: [],
                cluster_switch: false,
                clusters: []
            }}
            onSubmit={values =>
                setTimeout(() => {
                alert(JSON.stringify(values, null, 2));
                }, 500)
            }
            render={({ values }) => (
                <Bs.Container className="mt-5 mb-5">
                    <Bs.Card>
                        <Bs.Card.Body>
                            <Bs.Card.Title>Extract Script for Transport</Bs.Card.Title>
                                <Form>
                                    <Bs.Row>
                                        <InputWithLabel
                                            comp={Bs.Form.Control}
                                            type="text"
                                            id="reqst_cd"
                                            name="reqst_cd"
                                            placeholder="Request Code"
                                            label="Request Code"
                                        />
                                    </Bs.Row>
                                    <MultipleInput value={values.workflows} name="workflows" item="workflow" placeholder="Workflow Code" button="Add Workflow" label="Workflow" />
                                    {/* <ReactSwitch label="With Cluster?" name="cluster_switch" id="cluster_switch" col="5" /> */}
                                    <MultipleInput value={values.clusters} name="clusters" item="cluster" placeholder="Cluster Code" button="Add Cluster" label="Cluster" />
                                    <RegularButton col="5" type="submit" size="lg" text="Extract" />
                                </Form>
                        </Bs.Card.Body>
                    </Bs.Card>
                </Bs.Container>
            )}
        />
    );
};

export default ReactCard;
