import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import * as Bs from 'react-bootstrap';
import {FaPlus, FaMinus} from 'react-icons/fa';
import { useFormik, Form, Formik, Field, FieldArray } from 'formik';
import '../App.css';



function BasicInput(props){
    return(
        <>
            <Bs.Row>
                <Bs.Col md={props.Bs.Col}>
                    <Bs.Form.Group>
                        <Bs.Form.Control 
                            name={props.name} 
                            id={props.id} 
                            type={props.type}
                            placeholder={props.placeholder}
                        />
                    </Bs.Form.Group>
                </Bs.Col>
            </Bs.Row>
        </>
    );
}

function SideButton(props){
    var icon = "";
    var variant = "";
    if(props.use === "add"){
        icon = <FaPlus />;
        variant = "outline-primary";
    }
    else{
        icon = <FaMinus />;
        variant = "outline-danger";
    }
    return(
        <>
            <Bs.Col md={props.col}>
                <Bs.Form.Group>
                    <Bs.Button variant={variant} className="btn-circle" onClick={props.onClick}>{icon}</Bs.Button>
                </Bs.Form.Group>
            </Bs.Col>
        </>
    );
}

function RegularButton(props){
    var className = "mx-auto text-center";
    if(props.type !== "submit"){
        className = "";
    }

    return(
        <>
            <Bs.Col md={props.col} className={className}>
                <Bs.Form.Group>
                    <Bs.Button variant="outline-primary" size={props.size} type={props.type} onClick={props.onClick}>{props.text}</Bs.Button>
                </Bs.Form.Group>
            </Bs.Col>
        </>
    );
}

function ReactSwitch(props){
    return(
        <>
            <Bs.Col md={props.Bs.Col}>
                <Bs.Form.Group>
                    <Bs.Form.Check 
                        type="switch"
                        id={props.id}
                        name={props.name}
                        label={props.label}
                        checked={props.checked}
                        onChange={props.onChange}
                    />
                </Bs.Form.Group>
            </Bs.Col>
        </>
    );
}

function MultipleInput(props){
    return(
        <>
            <FieldArray
                name={props.name}
                render={arrayHelpers => (
                <div>
                    {props.value.workflows && props.value.workflows.length > 0 ? (
                    props.value.workflows.map((value, index) => (
                        <Bs.Row key={index}>
                            <Bs.Col md="6">
                                <Field as={Bs.Form.Control} name={`${props.name}.${index}`} placeholder={props.placeholder} />
                            </Bs.Col>
                            <SideButton
                                col="1"
                                use="subtract"
                                onClick={() => arrayHelpers.remove(index)} // remove a friend from the list
                            />
                            <SideButton
                                col="1"
                                use="add"
                                onClick={() => arrayHelpers.push('')} // remove a friend from the list
                            />
                        </Bs.Row>
                    ))
                    ) : 
                    (
                        <Bs.Row>
                            <RegularButton type="button" size="small" onClick={() => arrayHelpers.push('')} text="Add Workflow" />
                        </Bs.Row>
                    )}
                </div>
                )}
            />
        </>
    );
}

export  {
    BasicInput,
    SideButton,
    RegularButton,
    ReactSwitch,
    MultipleInput
};
