import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import {FaPlus} from 'react-icons/fa';
import '../index.css';

function BasicInput(props){
    return(
        <>
            <Col md={props.col}>
                <Form.Group>
                    <Form.Control type={props.type} placeholder={props.placeholder} name={props.name} id={props.id} />
                </Form.Group>
            </Col>
        </>
    );
}

function SideButton(props){
    return(
        <>
            <Col md={props.col}>
                <Form.Group>
                    <Button style="wid" variant="outline-primary"><FaPlus /></Button>
                </Form.Group>
            </Col>
        </>
    );
}

export  {
    BasicInput,
    SideButton
};
