import React from 'react';
import { Form, Formik } from 'formik';
import 'bootstrap/dist/css/bootstrap.min.css';
import * as Bs from 'react-bootstrap';
import * as Yup from 'yup';

import { RegularButton, MultipleInput, InputWithLabel} from './FormComponents.js';

const ValidForm = Yup.object().shape({
        reqst_cd: Yup.string()
            .required('Request code is required.'),
        workflows: Yup.array()
            .of(
                Yup.string()
                .required('Workflow code is required.'),
            )
            .min(1, 'Minimum of 1 workflow.'),
        clusters: Yup.array()
            .of(
                Yup.string()
                .required('Cluster code is required.')
            ),
        cluster_apvls: Yup.array()
            .of(
                Yup.string()
                .required('Cluster Approval code is required.')
            ),
        dropdowns: Yup.array()
            .of(
                Yup.string()
                .required('Field Name is required.')
            ),
        apps_cd: Yup.string(),
        apps_owners: Yup.array()
        .of(
            Yup.string()
            .required('Application Owner is required.'),
        )
        .when('apps_cd', {
            is: (apps_cd) => apps_cd > '',
            then: Yup.array()
                .min(2, 'Minimum of 2 Application Owners.'),          
        })
        
});

class ReactCard extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
          users: [],
          apps_owners: []
        };

        this.appsCdChangeHandler = this.appsCdChangeHandler.bind(this);
    }

    componentDidMount() {
        fetch("http://10.15.10.250:8080/extract_dev2/users")
        .then(res => res.json())
        .then(
            (result) => {
                console.log(result)
                this.setState({
                    // users: result
                    // isLoaded: true,
                    // selectedOption: "Please choose"
                });
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
                this.setState({
                    error
                });
                console.log(this.state.error)
            }
        )

    }

    appsCdChangeHandler(event){
        if(event.target.value == '')
        this.setState({
            apps_owners: []
        });
    }

    render(){
    return(
        <>
        <Formik
            initialValues={{ 
                users: this.state.users,
                reqst_cd: '',
                workflows: [],
                cluster_switch: false,
                clusters: [],
                cluster_apvls: [],
                dropdowns: [],
                apps_cd: '',
                apps_owners: this.state.apps_owners
            }}
            enableReinitialize
            validationSchema={ValidForm}
            onSubmit={values =>
                console.log(values)
            }
        >
            {({ 
                handleSubmit,
                handleChange,
                handleBlur,
                values,
                touched,
                isValid,
                errors,
                form
            }) => (
                <Bs.Container className="mt-5 mb-5">
                    <Bs.Card>
                        <Bs.Card.Body>
                            <Bs.Card.Title>Extract Script for Transport</Bs.Card.Title>
                                <Form>
                                    <Bs.Row>
                                        <InputWithLabel
                                            comp={Bs.Form.Control}
                                            type="text"
                                            id="reqst_cd"
                                            name="reqst_cd"
                                            placeholder="Request Code"
                                            label="Request Code"
                                            errors={errors.reqst_cd}
                                            touched={touched.reqst_cd}
                                        />
                                    </Bs.Row>
                                    <MultipleInput value={values.workflows} errors={errors.workflows} touched={touched.workflows} name="workflows" item="workflow" placeholder="Workflow Code" button="Add Workflow" label="Workflow" />
                                    {/* <ReactSwitch label="With Cluster?" name="cluster_switch" id="cluster_switch" col="5" /> */}
                                    <MultipleInput value={values.clusters} errors={errors.clusters} touched={touched.clusters} name="clusters" item="cluster" placeholder="Cluster Code" button="Add Cluster" label="Cluster" />
                                    <MultipleInput value={values.cluster_apvls} errors={errors.cluster_apvls} touched={touched.cluster_apvls} name="cluster_apvls" item="cluster_apvls" placeholder="Cluster Approval Code" button="Add Cluster Approver" label="Cluster Approval" />
                                    <MultipleInput value={values.dropdowns} errors={errors.dropdowns} touched={touched.dropdowns} name="dropdowns" item="dropdowns" placeholder="Field Name" button="Add Dropdown" label="Dropdown" />
                                    <Bs.Row>
                                        <Bs.Col md="6">
                                            <Bs.Form.Group>
                                                <FormLabel label={props.label} />
                                                <Field 
                        as={props.comp} 
                        type={props.type}
                        id={props.id}
                        name={props.name}
                        placeholder={props.placeholder}
                        onChange={props.onChange}
                    />
                                                <div className="text-danger">
                                                    <ErrorMessage name={props.name} />
                                                </div>
                                            </Bs.Form.Group>
                                    </Bs.Col>
                                        <InputWithLabel
                                            comp={Bs.Form.Control}
                                            type="text"
                                            id="apps_cd"
                                            name="apps_cd"
                                            placeholder="Application Code"
                                            label="Application Code"
                                            onChange={this.appsCdChangeHandler}
                                            errors={errors.apps_cd}
                                            touched={touched.apps_cd}
                                        />
                                    </Bs.Row>
                                    <MultipleInput value={values.apps_owners} errors={errors.apps_owners} touched={touched.apps_owners} name="apps_owners" item="apps_owners" placeholder="Application Owner" button="Add Application Owner" label="Application Owner" />
                                    <RegularButton col="5" type="submit" size="lg" text="Extract" />
                                    
                                </Form>
                        </Bs.Card.Body>
                    </Bs.Card>
                </Bs.Container>
            )}
        </Formik>
        </>
    );}
};

export default ReactCard;
